function make_file($dept_name,$dept_data)
{
    $base_file = file_get_contents('generic_event.html');
    $nums = array("first","second","third","forth","fifth","sixth","seventh","eighth","ninth","tehtn","eleventh");
    foreach ( $nums as $num) {

        if(array_key_exists($num,$dept_data)){
            if(is_array($dept_data[$num])){
                $event = $dept_data[$num];
                $event_details = array
                (
                    '{{name}}' => (array_key_exists('name',$event)?$event['name']:'Not Defined'),
                    '{{tagline}}' => (array_key_exists('tagline',$event)?$event['tagline']:'Not Defined'),
                    '{{description}}' => (array_key_exists('description',$event)?$event['description']:'Not Defined'),
                    '{{rules}}' => (array_key_exists('rules',$event)?$event['rules']:'Not Defined'),
                    '{{teamsize}}' => (array_key_exists('teamsize',$event)?$event['teamsize']:'Not Defined'),
                    '{{date}}' => (array_key_exists('date',$event)?$event['date']:'Not Defined'),
                    '{{time}}' => (array_key_exists('time',$event)?$event['time']:'Not Defined'),
                    '{{venue}}' => (array_key_exists('venue',$event)?$event['venue']:'Not Defined'),
                    '{{fees}}' => (array_key_exists('fees',$event)?$event['fees']:'Not Defined'),
                    '{{contact}}' => (array_key_exists('contact',$event)?$event['contact']:'Not Defined')
                );
                $markup = str_replace(array_keys($event_details), array_values($event_details), $base_file);
                $file_name = $dept_name.'_'.$num.'.html';
                file_put_contents($file_name,$markup);
            }
        }
    }
}

